- Market appeal

Decide if quality is sufficient (done = Yes) or needs more work (done = No).

parameters.jsCode:
// Process evaluation results and prepare for next iteration
let processedResult;

try {
  const evaluation = $json.output || {};

  // Get CURRENT turn from the loop data
  const loopData = $('Initialize Loop Data').item.json;
  const currentTurn = loopData.turn || 1;
  const maxTurns = loopData.max_turns || 3;

  // Increment the turn counter
  const nextTurn = currentTurn + 1;

  // Extract evaluation data with fallbacks
  const ideas = evaluation.ideas || [];
  const done = evaluation.done || "No";
  const qualityAssessment = evaluation.quality_assessment || "No assessment provided";
  const recommendations = evaluation.recommendations || "Continue refinement";

  // Force completion if we've hit max turns or have no ideas
  const shouldForceComplete = nextTurn > maxTurns || ideas.length === 0;
  const finalDone = shouldForceComplete ? "Yes" : done;

  console.log(`TURN PROCESSING: ${currentTurn} -> ${nextTurn}, Done: ${finalDone}, Max: ${maxTurns}`);

  processedResult = {
    ideas: ideas,
    turn: nextTurn,
    done: finalDone,
    topic: loopData.topic,
    research_data: loopData.research_data,
    max_turns: maxTurns,
    quality_assessment: qualityAssessment,
    recommendations: recommendations,
    iteration_complete: currentTurn
  };

} catch (error) {
  // Error handling - force completion
  console.error('Process Iteration Error:', error);
  processedResult = {
    ideas: [],
    turn: 99, // Force high number to trigger exit
    done: "Yes",
    topic: "Error Processing",
    research_data: "Error occurred",
    max_turns: 3,
    quality_assessment: "Processing error occurred",
    recommendations: "Please review workflow configuration",
    error: error.message
  };
}

return [{ json: processedResult }];

